Next(Math.Min(startY, endY), Math.Max(startY, endY)); // –ü–ª–∞–≤–Ω–æ–µ –ø–µ—Ä–µ–º–µ—â–µ–Ω–∏–µ –∫—É—Ä—Å–æ—Ä–∞ –æ—Ç –Ω–∞—á–∞–ª–∞ –¥–æ –∫–æ–Ω—Ü–∞ for (int i = 0; i <= steps; i++) { double t = (double)i / steps; double xt = (1 - t) * (1 - t) * startX + 2 * (1 - t) * t * ctrlX + t * t * endX; double yt = (1 - t) * (1 - t) * startY + 2 * (1 - t) * t * ctrlY + t * t * endY; SetCursorPos((int)xt, (int)yt); Thread.Sleep(1); } } public long? CheckPrice() { using (Bitmap bmpScreenshot = new Bitmap(115, 20, PixelFormat.Format32bppArgb)) using (Graphics gfxScreenshot = Graphics.FromImage(bmpScreenshot)) { gfxScreenshot.CopyFromScreen(1335, 325, 0, 0, new Size(115, 20), CopyPixelOperation.SourceCopy); if (IsOurNumber(bmpScreenshot)) { // –ï—Å–ª–∏ —á–∏—Å–ª–æ –Ω–∞—à–µ, –º—ã –Ω–µ –¥–µ–ª–∞–µ–º –Ω–∏—á–µ–≥–æ –∏ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º null return null; } else { // –ï—Å–ª–∏ —á–∏—Å–ª–æ –Ω–µ –Ω–∞—à–µ, —Ä–∞—Å–ø–æ–∑–Ω–∞–µ–º –µ–≥–æ –∏ –ø—Ä–∏–±–∞–≤–ª—è–µ–º 1 return RecognizeNumberAndAddOne(bmpScreenshot); } } } // –î–æ–≤–µ—Ä–∏—Ç–µ–ª—å–Ω—ã–π –∏–Ω—Ç–µ—Ä–≤–∞–ª –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ –ø–∏–∫—Å–µ–ª—è public bool IsColorSimilar(Color color1, Color color2, int tolerance) { return Math.Abs(color1.R - color2.R) <= tolerance && Math.Abs(color1.G - color2.G) <= tolerance && Math.Abs(color1.B - color2.B) <= tolerance; } // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ —Ç–æ —á—Ç–æ –≤—ã—à–µ –ª–∏ –Ω–∞—à–∞ —Ü–µ–Ω–∞ public bool IsOurNumber(Bitmap bmp) { // –¶–≤–µ—Ç —á–∏—Å–ª–∞, –∫–æ–≥–¥–∞ –æ–Ω–æ —è–≤–ª—è–µ—Ç—Å—è \\"–Ω–∞—à–∏–º\\" Color ourNumberColor = Color.FromArgb(255, 182, 153, 127); // –î–æ–ø—É—Å–∫ –ø–æ –∫–∞–∂–¥–æ–º—É –∏–∑ —Ü–≤–µ—Ç–æ–≤—ã—Ö –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ int tolerance = 3; // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ü–≤–µ—Ç –ø–∏–∫—Å–µ–ª—è –≤ –Ω–∏–∂–Ω–µ–º –ø—Ä–∞–≤–æ–º —É–≥–ª—É (—á—É—Ç—å –≤–Ω—É—Ç—Ä–∏ –æ—Ç –∫—Ä–∞—è) // –£—á–∏—Ç—ã–≤–∞—è, —á—Ç–æ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –Ω–∞—á–∏–Ω–∞—é—Ç—Å—è —Å 0, (98, 18) –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –≤ –∫—Ä–∞—é Color pixelColor = bmp.GetPixel(98, 18); return IsColorSimilar(ourNumberColor, pixelColor, tolerance); } public long? RecognizeNumberAndAddOne(Bitmap bitmapImage) { try { // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º –∏ —É–ª—É—á—à–∞–µ–º –∫–æ–Ω—Ç—Ä–∞—Å—Ç –ø–µ—Ä–µ–¥ OCR using (Bitmap resultImage = EnhanceImage(bitmapImage)) { // –°–æ–∑–¥–∞–µ–º –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ Emgu.CV –∏–∑ Bitmap using (Image<Bgr, byte> img = new Image<Bgr, byte>(resultImage)) { using (Tesseract tesseract = new Tesseract(@\\"MainMenu/tessdata\\", \\"eng\\", OcrEngineMode.TesseractLstmCombined)) { // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Ä–µ–∂–∏–º —Ä–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏—è —Ç–æ–ª—å–∫–æ –¥–ª—è —Ü–∏—Ñ—Ä tesseract.SetVariable(\\"tessedit_char_whitelist\\", \\"0123456789\\"); // –ü—Ä–∏–º–µ–Ω—è–µ–º OCR –Ω–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ tesseract.SetImage(img); tesseract.Recognize(); // –ü–æ–ª—É—á–∞–µ–º —Ä–∞—Å–ø–æ–∑–Ω–∞–Ω–Ω—ã–π —Ç–µ–∫—Å—Ç string recognizedText = tesseract.GetUTF8Text().Trim(); // –ü—ã—Ç–∞–µ–º—Å—è –ø—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞—Ç—å —Ç–µ–∫—Å—Ç –≤ —á–∏—Å–ª–æ if (long.TryParse(recognizedText, out long number)) { img.Dispose(); bitmapImage.Dispose(); tesseract.Dispose(); // –ü—Ä–∏–±–∞–≤–ª—è–µ–º 1 –∫ —á–∏—Å–ª—É return number + 1; } } } } } catch (Exception ex) { // –û–±—Ä–∞–±–æ—Ç–∫–∞ –∏—Å–∫–ª—é—á–µ–Ω–∏–π MessageBox.Show(\\"An error occurred: \\" + ex.Message); } // –í–æ–∑–≤—Ä–∞—â–∞–µ–º null, –µ—Å–ª–∏ —á–∏—Å–ª–æ –Ω–µ –±—ã–ª–æ —Ä–∞—Å–ø–æ–∑–Ω–∞–Ω–æ return null; } public Bitmap EnhanceImage(Bitmap originalImage) { // –ö–æ–Ω–≤–µ—Ä—Ç–∞—Ü–∏—è –≤ –æ—Ç—Ç–µ–Ω–∫–∏ —Å–µ—Ä–æ–≥–æ Bitmap grayImage = new Bitmap(originalImage.Width, originalImage.Height); using (Graphics g = Graphics.FromImage(grayImage)) { ColorMatrix colorMatrix = new ColorMatrix( new float[][] { new float[] {.3f, .3f, .3f, 0, 0}, new float[] {.59f, .59f, .59f, 0, 0}, new float[] {.11f, .11f, .11f, 0, 0}, new float[] {0, 0, 0, 1, 0}, new float[] {0, 0, 0, 0, 1} }); using (ImageAttributes attributes = new ImageAttributes()) { attributes.SetColorMatrix(colorMatrix); g.DrawImage(originalImage, new Rectangle(0, 0, originalImage.Width, originalImage.Height), 0, 0, originalImage.Width, originalImage.Heig