e // %K line #property indicator_color2 Red //Yellow // DimGray // %D line #property indicator_color3 Green // C'255,255,255' // White // %K line of the current candle #property indicator_color4 Red //Yellow // DimGray // %D line of the current candle #property indicator_level1 80 #property indicator_level2 20 #property indicator_level3 50 #property indicator_levelcolor DarkSlateGray #property indicator_maximum 100 #property indicator_minimum 0 //---- input parameters extern bool NextHigherAuto = true; extern bool HidePctK = false; extern bool HidePctD = false; extern int TimeFrame = 0, // {1=M1, 5=M5, 15=M15, ..., 1440=D1, 10080=W1, 43200=MN1} KPeriod = 11, DPeriod = 3, Slowing = 3, MAMethod = 0, // {0=SMA, 1=EMA, 2=SMMA, 3=LWMA} PriceField = 0; // {0=Hi/Low, 1=Close/Close} extern bool ShowClock = false; // display time to candle close countdown extern color ClockColor = Red; //---- indicator buffers double BufferK[], BufferD[], BufferK_Curr[], BufferD_Curr[]; double ArrowUp[], ArrowDown[]; // Buffers for arrows //---- string IndicatorName = \\"\\", TimeLabelName = \\"\\"; #define arrowCodeUp 233 // Wingdings character code for arrow up #define arrowCodeDown 234 // Wingdings character code for arrow down //+------------------------------------------------------------------+ //| Custom indicator initialization function | //+------------------------------------------------------------------+ int init() { if(TimeFrame==0) TimeFrame=Period(); if(NextHigherAuto) TimeFrame=GetHigherTimeFrame(TimeFrame); //---- name for DataWindow and indicator subwindow label switch(TimeFrame) { case 1: IndicatorName=\\"Period M1\\"; break; case 5: IndicatorName=\\"Period M5\\"; break; case 15: IndicatorName=\\"Period M15\\"; break; case 30: IndicatorName=\\"Period M30\\"; break; case 60: IndicatorName=\\"Period H1\\"; break; case 240: IndicatorName=\\"Period H4\\"; break; case 1440: IndicatorName=\\"Period D1\\"; break; case 10080: IndicatorName=\\"Period W1\\"; break; case 43200: IndicatorName=\\"Period MN1\\"; break; default: {TimeFrame=Period(); init(); return(0);} } IndicatorName=IndicatorName+\\" Stoch(\\"+KPeriod+\\",\\"+DPeriod+\\",\\"+Slowing+\\")\\"; IndicatorShortName(IndicatorName); IndicatorDigits(1); string TimeFrameStr=TF2Str(TimeFrame); //---- indicator lines SetIndexBuffer(0,BufferK); SetIndexBuffer(1,BufferD); SetIndexBuffer(2,BufferK_Curr); SetIndexBuffer(3,BufferD_Curr); SetIndexStyle(0,DRAW_LINE,EMPTY,1); SetIndexStyle(2,DRAW_LINE,EMPTY,1); if(!HidePctK) { SetIndexStyle(0,DRAW_LINE,EMPTY,1); SetIndexStyle(2,DRAW_LINE,EMPTY,1); SetIndexLabel(0,TimeFrameStr+\\" %K line\\"); SetIndexLabel(2,TimeFrameStr+\\" %K CurrBar\\"); } else { SetIndexStyle(0,DRAW_NONE); SetIndexStyle(2,DRAW_NONE); SetIndexLabel(0,NULL); SetIndexLabel(2,NULL); } if(!HidePctD) { SetIndexStyle(1,DRAW_LINE,EMPTY,1); SetIndexStyle(3,DRAW_LINE,EMPTY,1); SetIndexLabel(1,TimeFrameStr+\\" %D Signal\\"); SetIndexLabel(3,TimeFrameStr+\\" %D CurrBar\\"); } else { SetIndexStyle(1,DRAW_NONE); SetIndexStyle(3,DRAW_NONE); SetIndexLabel(1,NULL); SetIndexLabel(3,NULL); } // Buffer per le frecce SetIndexBuffer(4, ArrowUp); SetIndexBuffer(5, ArrowDown); SetIndexArrow(4, arrowCodeUp); // This will show an upward arrow SetIndexArrow(5, arrowCodeDown); // This will show a downward arrow SetIndexStyle(4, DRAW_ARROW); SetIndexStyle(5, DRAW_ARROW); } //±-----------------------------------------------------------------+ //+------------------------------------------------------------------+ int deinit() { if(TimeLabelName!=\\"\\") if(ObjectFind (TimeLabelName) != -1) ObjectDelete(TimeLabelName); } //+------------------------------------------------------------------+ //| MTF Stochastic | //+------------------------------------------------------------------+ int start() { int counted_bars = IndicatorCounted(); // Verifica se ci sono abbastanza barre if (Bars <= KPeriod + DPeriod + Slowing) return(0); // Ottimizzazione. Il valore di counted_bars è maggiore di 0 se abbiamo già calcolato in precedenza delle barre if (counted_bars > 0) counted_bars--; int limit = Bars - counted_bars; // Quante barre devono essere calcolate // Calcola la linea %K e la linea %D dello stocastico per ogni barra necessaria for (int i = 0; i < limit; i++) { // Puoi accedere ai valori dello stocastico attraverso i buffer: BufferK[] e BufferD[] BufferK[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_MAIN, i); BufferD[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_SIGNAL, i); // Azzera i buffer delle frecce come predefinito ArrowUp[i] = EMPTY_VALUE; ArrowDown[i] = EMPTY_VALUE; // Controlla se c’è una condizione di incrocio valida if(i > 0) { // Disegna una freccia verso l’alto se la linea %K incrocia al rialzo sopra la linea %D if(BufferK[i] > BufferD[i] && BufferK[i - 1] <= BufferD[i - 1]) ArrowUp[i] = Low[i] - (SymbolInfoDouble(Symbol(), SYMBOL_POINT) * 5); // Se vero, posiziona una freccia sotto il minimo della barra corrente // Disegna una freccia verso il basso se la linea %K incrocia al ribasso sotto la linea %D if(BufferK[i] < BufferD[i] && BufferK[i - 1] >= BufferD[i - 1]) ArrowDown[i] = High[i] + (SymbolInfoDouble(Symbol(), SYMBOL_POINT) * 5); // Se vero, posiziona una freccia sopra il massimo della barra corrente } } // Calcola il valore dell’attuale barra incompleta, che non è incluso nel ciclo sopra int last_uncalculated = limit - 1; if (last_uncalculated >= 0) { BufferK[last_uncalculated] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_MAIN, last_uncalculated); BufferD[last_uncalculated] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_SIGNAL, last_uncalculated); } return(0); } //±-----------------------------------------------------------------+ string TF2Str(int iPeriod) { switch(iPeriod) { case PERIOD_M1: return(\\"M1\\"); case PERIOD_M5: return(\\"M5\\"); case PERIOD_M15: return(\\"M15\\"); case PERIOD_M30: return(\\"M30\\"); case PERIOD_H1: return(\\"H1\\"); case PERIOD_H4: return(\\"H4\\"); case PERIOD_D1: return(\\"D1\\"); case PERIOD_W1: return(\\"W1\\"); case PERIOD_MN1: return(\\"MN1\\"); default: return(\\"M\\"+iPeriod); } return(0); } int GetHigherTimeFrame(int CurrentPeriod ) { if ( CurrentPeriod == 0 ) CurrentPeriod = Period(); if( CurrentPeriod == 1 ) { return(5); } else if( CurrentPeriod == 5 ) { return(15); } else if( CurrentPeriod == 15 ) { return(30); } else if( CurrentPeriod == 30 ) { return(60); } else if( CurrentPeriod == 60 ) { return(240); } else if( CurrentPeriod == 240 ) { return(1440); } else if( CurrentPeriod == 1440 ) { return(10080); } else if( CurrentPeriod == 10080 ) { return(43200); } else if( CurrentPeriod == 43200 ) { return(43200); } } // int GetHigherTimeFrame()","output":"Dopo aver esaminato il codice, sembra che tutti i punti principali per la configurazione delle frecce siano corretti: - I buffer per le frecce sono definiti e impostati correttamente. - \`SetIndexArrow\` e \`SetIndexStyle\` sono utilizzati correttamente per definire lo stile delle frecce. - La logica di calcolo e inserimento delle frecce sembra corretta. Tuttavia, c'è una discrepanza nella funzione \`start()\` che potrebbe causare il problema di visualizzazione delle frecce. Eccola corretta: \`\`\`mql4 int start() { int counted_bars = IndicatorCounted(); // Check for sufficient bars if (Bars <= KPeriod + DPeriod + Slowing) return(0); if (counted_bars > 0) counted_bars--; int limit = Bars - counted_bars; for (int i = limit - 1; i >= 0; i--) { BufferK[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_MAIN, i); BufferD[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_SIGNAL, i); if(i < Bars - 1) // Do not check for the current bar being formed { // Bullish crossover if(BufferK[i] > BufferD[i] && BufferK[i+1] <= BufferD[i+1]) { ArrowUp[i] = Low[i] - (4 * Point); ArrowDown[i] = EMPTY_VALUE; } // Bearish crossover if(BufferK[i] < BufferD[i] && BufferK[i+1] >= BufferD[i+1]) { ArrowDown[i] = High[i] + (4 * Point); ArrowUp[i] = EMPTY_VALUE; } } } return(0); } \`\`\` Modifiche principali: - Il loop itera ora in ordine inverso (\`for (int i = limit - 1; i >= 0; i--)\`), che è più tipico in MQL4 per i calcoli di indicatori perché partiamo dalla barra più recente e torniamo indietro. - \`i < Bars - 1\` ora è usato per escludere il calcolo per la barra corrente essendo formata, che può ancora non avere dati sufficienti per un crossover validato. Una volta apportate queste modifiche, ricompila il codice completamente e testalo sulla piattaforma MT4 per assicurarti che le frecce vengano visualizzate come previsto. Se continui ad avere problemi con le frecce che non vengono visualizzate, potrebbe anche essere utile inserire alcune istruzioni di stampa di debug all'interno del loop per verificare che i valori dello stocastico vengano calcolati come previsto e che le condizioni degli incroci siano soddisfatte. Puoi fare questo con \`Print(\\"Valore: \\", variabile);\`."},{"instruction":"scrivi tutta la funzione per intero","output":"Certo, ecco la funzione \`start()\` riscritta per intero con le correzioni suggerite: \`\`\`mql4 int start() { // Conteggio delle barre elaborate int counted_bars = IndicatorCounted(); // Assicurarsi di avere abbastanza barre per eseguire i calcoli if (Bars <= KPeriod + DPeriod + Slowing) return(0); // Se abbiamo già calcolato delle barre, dobbiamo sottrarre 1 da counted_bars per ricontare l'ultima barra (che potrebbe avere un valore nuovo) if (counted_bars > 0) counted_bars--; // Impostiamo il limite di barre da calcolare int limit = Bars - counted_bars; // Inizia il ciclo dal valore della barra più recente calcolata e prosegui fino alla prima barra for (int i = limit - 1; i >= 0; i--) { // Calcolo dei valori dello stocastico per %K e %D BufferK[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_MAIN, i); BufferD[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_SIGNAL, i); // Mantieni le frecce vuote come predefinito in ogni loop per evitare vecchi segnali ArrowUp[i] = EMPTY_VALUE; ArrowDown[i] = EMPTY_VALUE; // Controlliamo solo se non siamo sulla barra corrente e c'è almeno una barra precedente da verificare if (i < Bars - 1) { // Crossover rialzista if (BufferK[i] > BufferD[i] && BufferK[i + 1] <= BufferD[i + 1]) { ArrowUp[i] = Low[i] - (4 * Point); // Posiziona la freccia verso l'alto } // Crossover ribassista if (BufferK[i] < BufferD[i] && BufferK[i + 1] >= BufferD[i + 1]) { ArrowDown[i] = High[i] + (4 * Point); // Posiziona la freccia verso il basso } } } return(0); } \`\`\` Questa versione della funzione \`start()\` dovrebbe calcolare gli incroci dello stocastico e posizionare le frecce correttamente sul grafico, assumendo che i buffer siano configurati correttamente. Ricorda di ricompilare il codice nell'editor MQL4 e di verificarne il funzionamento direttamente sulla piattaforma di trading MetaTrader 4."},{"instruction":"manca tutto il resto della funzione!!!!","output":"Mi scuso per l'errore. Ecco la funzione \`start()\` completa e corretta, basata sul tuo codice originale, con le modifiche per assicurarti che sia le linee dello stocastico che le frecce agli incroci vengano visualizzate correttamente: \`\`\`mql4 //+------------------------------------------------------------------+ //| MTF Stochastic | //+------------------------------------------------------------------+ int start() { int counted_bars = IndicatorCounted(); // Check for sufficient bars if(Bars <= KPeriod + DPeriod + Slowing) return(0); if(counted_bars < 0) return(0); if(counted_bars > 0) counted_bars--; int limit = Bars - counted_bars; // Calculate stochastic for each needed bar for(int i = limit - 1; i >= 0; i--) { BufferK[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_MAIN, i); BufferD[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_SIGNAL, i); // Ensure we do not check current bar which is still forming if i=0 (on the first iteration) if(i > 0) { // Check for a bullish crossover (current %K crosses above current %D) if(BufferK[i] > BufferD[i] && BufferK[i-1] <= BufferD[i-1]) { ArrowUp[i] = Low[i] - (SymbolInfoDouble(Symbol(), SYMBOL_POINT) * 5); // Place an arrow below the Low of the bar } else { ArrowUp[i] = EMPTY_VALUE; // No arrow since no crossover } // Check for a bearish crossover (current %K crosses below current %D) if(BufferK[i] < BufferD[i] && BufferK[i-1] >= BufferD[i-1]) { ArrowDown[i] = High[i] + (SymbolInfoDouble(Symbol(), SYMBOL_POINT) * 5); // Place an arrow above the High of the bar } else { ArrowDown[i] = EMPTY_VALUE; // No arrow since no crossover } } } // Now handle the currently forming bar if (limit-1 >= 0) { int i = limit - 1; // Calculate stochastic for the current bar, which is still in progress BufferK[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_MAIN, i); BufferD[i] = iStochastic(NULL, TimeFrame, KPeriod, DPeriod, Slowing, MAMethod, PriceField, MODE_SIGNAL, i); } return(0); } //+------------------------------------------------------------------+ \`\`\` Ricorda di assicurarti che i colori dell'indicatore (le definizioni \`indicator_color\`) e i livelli (\`indicator_level1, indicator_level2, etc.\`) siano impostati correttamente in modo che le frecce possano essere visualizzate in base ai livelli desiderati. Ricompila il codice in MetaEditor e testalo sulla piattaforma MT4 per verificare che sia lo stocastico che le frecce siano visibili come previsto."},{"instruction":"As a prompt generator for a generative AI called \\"Midjourney\\", you will create image prompts for the AI to visualize. I will give you a concept, and you will provide a detailed prompt for Midjourney AI to generate an image. Please adhere to the structure and formatting below, and follow these gu