 //case SortField.PartsGroup: // s = s.OnField(‚ÄúgroupNames.sort‚Äù); // break; case SortField.AssemblyGroup: s = s.OnField(‚ÄúassemblyGroupNames‚Äù); s = s.Mode(SortMode.Min); break; default: throw new ArgumentOutOfRangeException(); } return s; }); if (request.OrderbyFilter.Field == SortField.AssemblyGroup) { sd = sd.Sort(s => { var order = request.OrderbyFilter.Direction == OrderDirection.Ascending ? SortOrder.Ascending : SortOrder.Descending; s = s.Order(order); s = s.OnField(sp => sp.SparePartId); return s; }); } } return sd; } private SearchDescriptor<MetadataEntity> GetFilterDescriptor(FilterType filterType, int facetTake, string text, string kgId, string languageKey) { var sd = new SearchDescriptor<MetadataEntity>().Skip(0).Take(facetTake).Query( fd => fd.Term(m => m.LanguageKey, languageKey) && fd.Term(m => m.Kg, kgId)); return sd.Query( fd => fd.Term(s => s.FilterType, filterType) && ( fd.QueryString(qd => qd.Query(EscapeText(text) + ‚Äú~‚Äù).OnFields(m => m.Name).FuzzyMinimumSimilarity(0.8)) || fd.QueryString(qd => qd.Query(MultiWordWildcard(text)).OnFields(m => m.Name).Boost(2)) )); }","output":"Âú®C#ËØ≠Ë®Ä‰∏≠Ôºå‰ΩøÁî®NEST 7.17.5Â∫ìÊõøÊç¢‰ª•‰∏ä‰ª£Á†ÅÔºåÂ∞ΩÈáèÂáèÂ∞ë‰øÆÊîπ„ÄÇ \`\`\`csharp using Nest; public SearchDescriptor<SparePartEntity> GetFullDescriptor(SearchRequest request) { var client = new ElasticClient(); var searchText = string.IsNullOrWhiteSpace(request.DynamicText) ? \\"*\\" : request.DynamicText.ToLower(); var escapedText = EscapeText(searchText); var fuzzySearchText = escapedText; var userInputs = searchText.Split(new[] { \\" \\" }, StringSplitOptions.RemoveEmptyEntries); if (userInputs.Length > 1) { fuzzySearchText = string.Join(\\"~ AND \\", userInputs) + \\"~\\"; } else { fuzzySearchText += \\"~\\"; } var searchDescriptor = new SearchDescriptor<SparePartEntity>().Skip(request.Skip).Take(request.Take) .Query(q => q .Bool(b => b .Filter(f => ApplyFilters(request, f)) .Must(qd => ApplyQueryFilters(qd, escapedText, fuzzySearchText, userInput